def baby_shark_lyrics():
  s, c = "",  " doo doo doo doo doo doo\n"
  l = ["Baby", "Mommy", "Daddy", "Grandma", "Grandpa"]
  for t in l:
    for i in range(3):
      s += t + " shark,"+c
    s+= t + " shark!\n"
  s += ("Let's go hunt," +c) * 3
  s += "Let's go hunt!\n"
  return s+"Run away,…"




print(baby_shark_lyrics())


